SELECT app_info.id,softwareName,APKName,softwareSize,b1.valueName as
flatformName ,categoryLevel1,a1.categoryName as
categoryLevel1Name,categoryLevel2,a2.categoryName as
categoryLevel2Name,categoryLevel3,a3.categoryName as
categoryLevel3Name,status,b2.valueName as
statusName,downloads,versionNo FROM app_info
INNER JOIN data_dictionary AS b1 ON b1.valueId=app_info.flatformId
INNER JOIN data_dictionary AS b2 ON b2.valueId=app_info.status

INNER JOIN app_category AS a1 ON a1.id =app_info.categoryLevel1
INNER JOIN app_category AS a2 ON a2.id =app_info.categoryLevel2
INNER JOIN app_category AS a3 ON a3.id =app_info.categoryLevel3

INNER JOIN app_version ON app_version.id=app_info.versionId
WHERE b1.typeName='所属平台' AND b2.typeName='APP状态'


	and softwareName like concat('%',#{appinfo.softwareName},'%')


	and b2.valueId=#{appinfo.STATUS}


	and b1.valueId=#{appinfo.flatformId}


	and a1.id=#{appinfo.categoryLevel1}


	and a2.id=#{appinfo.categoryLevel2}


	and a3.id=#{appinfo.categoryLevel3}

limit #{currentPageNo},#{pageSize}

